package hello_08

import spock.lang.Specification
import com.mine.hello.*

class AppTest extends Specification {
    def "application has a greeting"() {
        setup:
        def app = new App()

        when:
        def result = app.greeting

        then:
        result != null
    }

    def "sum test"() {
        when:
        def x = Utils.sum(2, 5)

        then:
        x == 7
    }
}
